import Navbar from "./Navbar";
import { useNavigate } from "react-router-dom";


import axios from "axios";

export default function Login() {
    var uri = "http://localhost:7000/";
    var navi = useNavigate();


    function mahima(e) {
        e.preventDefault();
        var data = new FormData(e.currentTarget);
        var email = data.get('_id');
        var password = data.get('password');

        // console.log(email);
        // console.log(password);

        var udat = { Email: email, Password: password }
        // console.log(logindata);

        axios.post(uri + "login", udat).then((succ) => {
            console.log(succ)
                // console.log(succ.data)
                // if(succ.data.Name == udat.Email){
                //     alert("User login")
                //     navi('/')

                // }else{
                //     alert("Please Register")
                // }


            if (succ.data ) {

                alert("User Login is successfully")
                // console.log(succ.data._id);
                navi("/")
                localStorage.setItem("useridlocal", succ.data._id);
            } else {
                alert("Please Register First");
            }

        })




        // var logindata = { Email: email, Password: password }
        // axios.post(uri + "login", logindata).then((succ) => {
        //     console.log("Data successfully send");
        // })

        e.target.reset();

        e.target.email.focus();
    
}

return (
    <>
            <Navbar />
            <div className="img2">
                <div className="col-lg-5 col-md-6 col-sm-6 col-xs-12">
                    <div className="alert alert-info r1">
                        <h2 className="text-center">Member Login</h2>
                        <form onSubmit={mahima}>

                            <div className="form-group">
                                <input type={'email'} name='id' className="form-control" placeholder="ID" />
                            </div>

                            <div className="form-group">
                                <input type={'password'} name='password' className="form-control" maxlength="8" placeholder="Password" />
                            </div>

                            <div className="form-group">
                                <input type={'submit'} className="btn btn-primary" value={'Login'} />
                            </div>

                        </form>
                    </div>
                </div>
            </div>
        </>
    )



}
