import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
// import Navbar from '../components/Navbars';
export default function Login() {
const [credentials, setCredentials] = useState({ email: "", password: "" })
  let navigate = useNavigate()

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { email, password } = credentials;

        const response = await fetch("/signin", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({email , password ,})
        })
        const data = await response.json()
        console.log(data);
        // console.log(json(data.message));
        if(!data.success){
            window.alert("Invalid Credentials");
        }
        else{
            window.alert("Login Successfull");
            let localStorage = window.localStorage;
            localStorage.setItem('userEmail' , credentials.email);
            localStorage.setItem('authToken' , data.authToken)
            navigate("/");
            
        }
    
  }

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value })
  }

  return (
    <div style={{backgroundImage: 'url("https://t4.ftcdn.net/jpg/06/08/65/03/360_F_608650310_Q7Jdo1q3h2hfev7FMSv0KEMNFO924jpJ.jpg")', height: '100vh', backgroundSize: 'cover' }}>
      <>
            <Navbar />

            <div className="img1">


                <div className="col-lg-6 col-md-6 col-sm-6 col-xs-10 ">
                    <div className="alert alert-info r1">
                        <center><h2>Registration</h2></center>

                        <form onSubmit={mahima}>
                            <div className="form-group">
                                <input type={'text'} name='name' className="form-control" placeholder="Full Name" maxlength="20" required/>
                            </div>

                            <div className="form-group">
                                <input type={'text'} name='username' className="form-control" placeholder="Username" maxlength="20" required/>
                            </div>

                            <div className="form-group">
                                <input type={'text'} name='email' className="form-control" placeholder="Email" required/>
                            </div>

                            <div className="form-group">
                                <input type={'password'} name='password' className="form-control" placeholder="Password" maxlength="8" required/>
                            </div>

                            <div className="form-group">
                                <input type={'password'} name='password2' className="form-control" placeholder="Confirm Password"  maxlength="8" required />
                            </div>

                            <div className="form-group">
                                <input type={'submit'} className="btn btn-primary" value={'Submit'} />
                            </div>

                        </form>
                    </div>
                </div>

            </div>
        </>
    </div>
  )
}
